"use strict";const boxen=require("boxen"),chalk=require("chalk"),logUpdate=require("log-update"),style={padding:1,borderColor:"blue",dimBorder:!0},box=o=>console.log(boxen(chalk.blue(`\n${o}\n`),style)),warningLog=o=>console.log(chalk.yellow(`⚠❗ ${o}\n`)),successLog=o=>console.log(chalk.green(`✅ ${o}\n`)),errorLog=(o,e)=>console.log(chalk.red(`❌ ERROR: ${e}\n`,o,"\n")),loadingLog=(o,e)=>logUpdate(chalk.blue(`\n\n${o} ⚡${e}⚡ ${o}\n\n`));module.exports={box:box,warningLog:warningLog,errorLog:errorLog,successLog:successLog,loadingLog:loadingLog,style:style};